import React, { useState } from 'react';
import {
  MDBValidation,
  MDBValidationItem,
  MDBInput,
  MDBBtn,
  MDBTextArea,
  MDBFile,
  MDBCheckbox,
  MDBModal,
  MDBModalHeader,
  MDBModalBody,
  MDBModalFooter
} from 'mdb-react-ui-kit';
import Axiosinstance from '../../config/Axiosinstances';
import { toastError, toastSuccess } from '../../constants/Plugin';
import { useNavigate } from 'react-router-dom';
import './addcourtform.css'; // Import custom CSS file for styling

export default function Addcourtform() {
  const [formValue, setFormValue] = useState({
    courtName: '',
    location: '',
    type: '',
    address: '',
    about: '',
    description: ''
  });

  const navigate = useNavigate();
  const [courtimage, setCourtimage] = useState(null);
  const [selectedimage, setSelectedimage] = useState('');
  const [modalOpen, setModalOpen] = useState(false); // State for controlling modal visibility

  const onChange = (e) => {
    setFormValue({ ...formValue, [e.target.name]: e.target.value });
  };

  const addfileData = (e) => {
    setCourtimage(e.target.files[0]);
    e.target.files[0]
      ? setSelectedimage(URL?.createObjectURL(e.target.files[0]) ?? null)
      : setSelectedimage(null);
  };

  const addCourtdata = () => {
    let fileData = new FormData();
    fileData.append('image', courtimage);

    Axiosinstance.post('/admin/addCourtData', fileData, { params: formValue }, { Headers: { 'content-Type': 'multipart/form-data' } })
      .then((response) => {
        toa('Court Data added successfully');
        navigate('/home');
      })
      .catch((err) => {
        toastError('Invalid entry');
        if (err.response.data.message === 'unauthorized user') {
          localStorage.clear();
          navigate('/');
        }
      });
  };

  return (
    <>
      <MDBBtn onClick={() => setModalOpen(true)}>Open Form Modal</MDBBtn>
      <MDBModal show={modalOpen} onHide={() => setModalOpen(false)}>
        <MDBModalHeader>Add Court Form</MDBModalHeader>
        <MDBModalBody>
          <MDBValidation className='row g-3'>
            {/* Your form inputs */}
          </MDBValidation>
        </MDBModalBody>
        <MDBModalFooter>
          <MDBBtn color='secondary' onClick={() => setModalOpen(false)}>
            Close
          </MDBBtn>
          <MDBBtn color='primary' onClick={addCourtdata}>
            Save changes
          </MDBBtn>
        </MDBModalFooter>
      </MDBModal>
    </>
  );
}
