import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import Axiosinstance from '../../config/Axiosinstances';
import { toastError, toastSucess } from '../../constants/Plugin';
import { useNavigate } from 'react-router-dom';

export default function AddCourtForm() {
  const [formValue, setFormValue] = useState({
    courtName: '',
    location: '',
    type: '',
    address: '',
    about:'',
    description:''
  });

  const [courtImage, setCourtImage] = useState(null);
  const [selectedImage, setSelectedImage] = useState('');
  const navigate = useNavigate();

  const onChange = (e) => {
    setFormValue({ ...formValue, [e.target.name]: e.target.value });
  };

  const addFileData = (e) => {
    setCourtImage(e.target.files[0]);
    e.target.files[0]
      ? setSelectedImage(URL?.createObjectURL(e.target.files[0]) ?? null)
      : setSelectedImage(null);
  };

  const addCourtData = () => {
    const fileData = new FormData();
    fileData.append('image', courtImage);

    Axiosinstance.post('/admin/addCourtData', fileData, {
      params: formValue,
      Headers: { 'content-Type': 'multipart/form-data' }
    })
      .then((response) => {
        toastSucess('Court Data added successfully');
        navigate('/home');
      })
      .catch((err) => {
        toastError('Invalid entry');
        if (err.response.data.message === 'unauthorized user') {
          localStorage.clear();
          navigate('/');
        }
      });
  };

  return (
    <Form className='mt-5 ms-4 me-4'>
      <Row className='mb-3'>
        <Col md={4}>
          <Form.Label>Court Name</Form.Label>
          <Form.Control
            type='text'
            name='courtName'
            value={formValue.courtName}
            onChange={onChange}
            required
          />
        </Col>
        {/* Repeat similar structures for other form inputs */}
      </Row>
      <Row className='mb-3'>
        <Col md={6}>
          <Form.Label>Court Image</Form.Label>
          <Form.Control
            type='file'
            name='courtImage'
            onChange={addFileData}
            required
          />
        </Col>
        {selectedImage && (
          <Col md={6}>
            <img src={selectedImage} alt='Selected Image' className='object-fit w-25 ml-5' />
          </Col>
        )}
      </Row>
      <Row className='mb-3'>
        <Col md={12}>
          <Form.Check
            label='Agree to terms and conditions'
            id='invalidCheck'
            required
          />
        </Col>
      </Row>
      <Button type='submit' onClick={addCourtData}>Submit form</Button>
      <Button type='reset' className='ms-4 '>Reset form</Button>
    </Form>
  );
}
