import React from 'react';
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardSubTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn,
  MDBRipple
} from 'mdb-react-ui-kit';
import { BASE_URL } from '../../constants/constants';
import { useNavigate } from 'react-router-dom';
import './cards.css'; // Import custom CSS file for styling

export default function Cards({ court }) {
  const navigate = useNavigate();

  return (
    <MDBCard className='custom-card' onClick={() => navigate(`/courtUserViewPage/${court._id}`)}>
      <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
        <MDBCardImage
          src={`${BASE_URL}/courts/${court.courtPic}`}
          fluid
          alt='...'
          style={{ maxWidth: '100%', height: 'auto', display: 'block', margin: '0 auto', padding: '5px' }}
        />
      </MDBRipple>
      <MDBCardBody className='custom-card-body'>
        <MDBCardTitle className='card-title'>{court?.courtName}</MDBCardTitle>
        <MDBCardSubTitle className='card-subtitle'>{court?.type}</MDBCardSubTitle>
        <MDBCardSubTitle className='card-subtitle'>{court?.location}</MDBCardSubTitle>
        <MDBCardText className='card-text'>{court.about}</MDBCardText>
        <MDBBtn>Button</MDBBtn>
      </MDBCardBody>
    </MDBCard>
  );
}
