import React from 'react';
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardSubTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn,
  MDBRipple
} from 'mdb-react-ui-kit';
import { BASE_URL } from '../../constants/constants';
import { useNavigate } from 'react-router-dom';
import './ards.css'; // Import custom CSS file for styling

export default function Cards({ court }) {
  const navigate = useNavigate();

  return (
    <MDBCard className='custom-card' onClick={() => navigate(`/courtUserViewPage/${court._id}`)}>
      <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
        <MDBCardImage src={`${BASE_URL}/courts/${court.courtPic}`} fluid alt='...' />
      </MDBRipple>
      <MDBCardBody className='custom-card-body'>
        <MDBCardTitle>{court?.courtName}</MDBCardTitle>
        <MDBCardSubTitle>{court?.type}</MDBCardSubTitle>
        <MDBCardSubTitle>{court?.location}</MDBCardSubTitle>
        <MDBCardText>{court.about}</MDBCardText>
        <MDBBtn>Button</MDBBtn>
      </MDBCardBody>
    </MDBCard>
  );
}
