import React, { useState } from 'react';
import {
  MDBValidation,
  MDBValidationItem,
  MDBInput,
  MDBBtn,
  MDBTextArea,
  MDBFile,
  MDBCheckbox
} from 'mdb-react-ui-kit';
import Axiosinstance from '../../config/Axiosinstances';
import { toastError, toastSuccess } from '../../constants/Plugin';
import { useNavigate } from 'react-router-dom';

export default function AddCourtForm() {
  const [formValue, setFormValue] = useState({
    courtName: '',
    location: '',
    type: '',
    about: '',
    description: '',
    address: ''
  });

  const [courtImage, setCourtImage] = useState(null);
  const [selectedImage, setSelectedImage] = useState('');
  const navigate = useNavigate();

  const onChange = (e) => {
    setFormValue({ ...formValue, [e.target.name]: e.target.value });
  };

  const addFileData = (e) => {
    setCourtImage(e.target.files[0]);
    e.target.files[0]
      ? setSelectedImage(URL?.createObjectURL(e.target.files[0]) ?? null)
      : setSelectedImage(null);
  };

  const addCourtData = () => {
    const fileData = new FormData();
    fileData.append('image', courtImage);

    Axiosinstance.post('/admin/addCourtData', fileData, {
      params: formValue,
      Headers: { 'content-Type': 'multipart/form-data' }
    })
      .then((response) => {
        t('Court Data added successfully');
        navigate('/home');
      })
      .catch((err) => {
        toastError('Invalid entry');
        if (err.response.data.message === 'unauthorized user') {
          localStorage.clear();
          navigate('/');
        }
      });
  };

  return (
    <MDBValidation className='row g-3 mt-5 ms-4 me-4'>
      <MDBValidationItem className='col-md-4'>
        <MDBInput
          value={formValue.courtName}
          name='courtName'
          onChange={onChange}
          id='validationCustom01'
          required
          label='Court Name'
        />
      </MDBValidationItem>
      {/* Other form fields */}
      <div className='col-12'>
        <MDBBtn type='submit' onClick={addCourtData}>Submit form</MDBBtn>
        <MDBBtn type='reset' className='ms-4 '>Reset form</MDBBtn>
      </div>
    </MDBValidation>
  );
}
